/*
 * @(#)TestMemmove.c	1.2 03/01/29
 * Compliance test for Java memmove functions used by CVM's porting layer.
 */
#include "javavm/include/porting/memory.h"

#define ARRAY_SIZE(x)   (sizeof(x)/sizeof(x[0]))

static void
initArray(CVMUint8 *array, int size, int start)
{
    int i;
    for (i = 0; i < size; i++) {
        array[i] = start + i + 1;
    }
}

static int testCount = 0;
static int testPassed = 0;

static CVMBool
verifyBytes(const CVMUint8 *testname,
            const CVMUint8 *array, const CVMUint8 *expected, int size)
{
    int i;
    testCount++;
    for (i = 0; i < size; i++) {
        if (array[i] != expected[i]) {
            int j;
            CVMconsolePrintf("FAILED: %s\n", testname);
            CVMconsolePrintf("       : Error starting at index %d\n", i);
            CVMconsolePrintf("       : expecting %d, actual %d\n",
                             expected[i], array[i]);
            CVMconsolePrintf("       : Expecting:\n");
            for (j = 0; j < size; j++) {
                if ((j % 8) == 0) {
                    CVMconsolePrintf("       :");
                }
                CVMconsolePrintf(" %3d,", expected[j]);
                if ((j % 8) == 7) {
                    CVMconsolePrintf("\n");
                }
            }
            if ((j % 8) != 0) {
                CVMconsolePrintf("\n");
            }
            CVMconsolePrintf("       : Actual:\n");
            for (j = 0; j < size; j++) {
                if ((j % 8) == 0) {
                    CVMconsolePrintf("       :");
                }
                CVMconsolePrintf(" %3d,", array[j]);
                if ((j % 8) == 7) {
                    CVMconsolePrintf("\n");
                }
            }
            if ((j % 8) != 0) {
                CVMconsolePrintf("\n");
            }
            return CVM_FALSE;
        }
    }
    testPassed++;
    CVMconsolePrintf("PASSED: %s\n", testname);
    return CVM_TRUE;
}

static void
testByteMemmove(int salign, int dalign, int length,
                const CVMUint8 *expected, int expected_size,
                CVMBool isByte)
{
    CVMUint8 src[150];
    CVMUint8 dest[150];
    CVMUint8 testname[150];
    CVMUint8 *srcAlignedStart;
    CVMUint8 *destAlignedStart;

    /* Find the 64-bit aligned starting point for src and dest arrays: */
    srcAlignedStart = (CVMUint8 *)(((CVMUint32)src + 7) & ~7);
    destAlignedStart = (CVMUint8 *)(((CVMUint32)dest + 7) & ~7);

    {
        CVMUint8 *s, *d;
        initArray(srcAlignedStart, 100, 100);
        initArray(destAlignedStart, 100, 0);
        s = srcAlignedStart + salign + 8;
        d = destAlignedStart + dalign + 8;
        if (isByte) {
            CVMmemmoveByte(d, s, length);
            sprintf(testname, "byte: salign %d dalign %d length %d",
                    salign, dalign, length);
        } else {
            CVMmemmoveBoolean(d, s, length);
            sprintf(testname, "boolean: salign %d dalign %d length %d",
                    salign, dalign, length);
        }
        verifyBytes(testname,
            destAlignedStart, expected, expected_size);
    }
}

