void testMemmove(void)
{
    testCount = 0;
    testPassed = 0;

    testByteBoolean(CVM_TRUE);
    testByteBoolean(CVM_FALSE);
    testCharShort(CVM_TRUE);
    testCharShort(CVM_FALSE);
    testIntFloat(CVM_TRUE);
    testIntFloat(CVM_FALSE);
    testLongDouble(CVM_TRUE);
    testLongDouble(CVM_FALSE);

    testOverlappingByteBoolean(CVM_TRUE);
    testOverlappingByteBoolean(CVM_FALSE);
    testOverlappingCharShort(CVM_TRUE);
    testOverlappingCharShort(CVM_FALSE);
    testOverlappingIntFloat(CVM_TRUE);
    testOverlappingIntFloat(CVM_FALSE);
    testOverlappingLongDouble(CVM_TRUE);
    testOverlappingLongDouble(CVM_FALSE);

    /* Dump Results: */
    CVMconsolePrintf("\n%d passed\n%d failed\n%d total\n",
                     testPassed, testCount - testPassed, testCount);

}

